document.addEventListener("DOMContentLoaded",function(){header=document.querySelector(".header"),main=document.querySelector(".main"),headerHeight=header.offsetHeight,oldPos=0,window.addEventListener("scroll",function(){newPos=window.scrollY,newPos>headerHeight&&oldPos>newPos?(header.classList.add("fixed"),main.classList.add("compensate-header")):(header.classList.remove("fixed"),main.classList.remove("compensate-header")),oldPos=newPos}),menuToggle=document.querySelector('[data-id="menu-toggle"'),nav=document.querySelector(".nav"),menuToggle.addEventListener("click",function(e){e.target.classList.toggle("active"),nav.classList.toggle("active")})});